import { type SkillBonuses } from './skills'
import { type Talent } from './talent'

export type Class = Readonly<{
  name: string,
  endurancePerLevel: number,
  healthMultiplier: number,
  accuracy: number,
  deflection: number,
  skillBonuses: SkillBonuses,
  baseAbility: Ability
  abilities: ReadonlyArray<Ability>
  phrases?: ReadonlyArray<Phrase>
  talents: ReadonlyArray<Talent>,
  spiritForms?: ReadonlyArray<SpiritForm>
  deities?: ReadonlyArray<Deity>
  orders?: ReadonlyArray<Order>
}>

export type Ability = Readonly<{
  name: string
  link: string
  imageLink: string
  level: number
  activation: 
    'Passive' | 
    'Active (1 per Encounter)' | 'Active (2 per Encounter)' |
    'Active (3 per Rest)' | 'Active (2 per Rest)'
  effects: ReadonlyArray<string> | string
  speed?: 
    'Instant' | 'Average'
  duration?: string
  area?: string
  description: string
}>

export type Phrase = Readonly<{
  name: string
  level: number
  link: string
  imageLink: string
  area?: string
  effects: ReadonlyArray<string> | string
  duration: string
  linger: string
  description: string
}>

export type SpiritForm = Readonly<{
  name: string
  link: string
  damageType: undefined
  companionOnly?: boolean
}>

type Behavior =
  'diplomatic' | 'honest' | 'benevolent' | 'passionate' | 
  'cruel' | 'deceptive' | 'stoic' | 'aggressive'

export type Order = Readonly<{
  name: string
  favoredBehavior?: [Behavior, Behavior]
  disfavoredBehavior?: [Behavior, Behavior]
  companionOnly?: boolean
}>

export type Deity = Readonly<{
  name: string
  favoredDispositions: [Behavior, Behavior]
  condemnedDispositions: [Behavior, Behavior]
}>

export type AnimalCompanion = Readonly<{
  name: string
  companionOnly?: boolean
}>

export type ClassKey = 'barbarian' | 'chanter'

